mapper.update:
update t_cart set
    num=#{num},
    modified_user=#{modifiedUser},
    modified_time=#{modifiedTime}
where cid=#{cid}

mapper.select:
select cid, uid, pid, t_cart.price, t_cart.num, title, t_product.price as realPrice, image from t_cart left join t_product on t_cart.pid = t_product.id where cid in (
#{cid}
) order by t_cart.created_time desc
